func UnmarshalCurrency(data []byte) ([]Currencies, error) {
    var r []Currencies
    err := json.Unmarshal(data, &r)
    return r, err
}

type Currencies struct {
    CurrencyName string `json:"currency_name"`
    Symbol       string `json:"symbol"`
    IsActive     bool   `json:"is_active"`
    ID           string `json:"id"`
    NewID        string `json:"new_id"`
}

func CustomMigrations() error {
    if DB.Where("id = 1").Find(&models.Role{}).RowsAffected == 0 {
        DB.Create(&models.Role{
            ID:     1,
            Name:   "admin",
            Status: true,
        })
    }

    if DB.Where("id = 1").Find(&models.User{}).RowsAffected == 0 {
        DB.Create(&models.User{
            ID:        1,
            Username:  "system",
            Email:     "system@exrate.com",
            RoleID:    1,
            Status:    true,
            FirstName: "System",
            LastName:  "Exrate",
        })
    }

    for k := range config.Env.Adapters {
        if DB.Where("name = ?", k).Find(&models.Adapter{}).RowsAffected == 0 {
            DB.Create(&models.Adapter{
                CreatedByID:   1,
                Name:          k,
                Configuration: nil,
                IsActive:      false,
            })
        }
    }

    c, err := UnmarshalCurrency([]byte(currenciesConst))
    if err != nil {
        logrus.Error(err)
    }

    for k := range c {
        if DB.Where("id = ?", c[k].ID).Find(&models.Currency{}).RowsAffected == 0 {
            currency := models.Currency{
                ID:            c[k].ID,
                IsActive:      c[k].IsActive,
                CreatedByID:   1,
                CurrencyName:  c[k].CurrencyName,
                Symbol:        c[k].Symbol,
                NewCurrencyID: c[k].NewID,
            }
            if err := DB.Create(&currency).Error; err != nil {
                logrus.Error(fmt.Errorf("Insert Error: %v ", err))
            }
        }
    }

    return nil
}
